void Calculator_Run_SwitchOff( S_SM_Calculator_t* pStateMachine )
{
  if ( pStateMachine != NULL )
  {
    __attribute__( ( unused ) ) bool doneMain = false;
    __attribute__( ( unused ) ) bool doneInput = false;
    __attribute__( ( unused ) ) bool doneProcessingUnit = false;
    __attribute__( ( unused ) ) bool doneOutput = false;

    pthread_mutex_lock( &pStateMachine->guard );

    if ( Calculator_IsIn_Main_State( pStateMachine ) )
    {
      if ( Calculator_IsIn_Input_Region( pStateMachine ) )
      {
        doneInput = true;

        if ( Calculator_IsIn_CalculatorKeypad_State( pStateMachine ) )
        {
          Calculator_Terminate( pStateMachine );
        }
        else
        {
          doneInput = false;
        }
      }

      if ( Calculator_IsIn_ProcessingUnit_Region( pStateMachine ) )
      {
        doneProcessingUnit = true;

        if ( Calculator_IsIn_Process_State( pStateMachine ) )
        {
          if ( Calculator_IsIn_EnterBaseDigits_State( pStateMachine ) )
          {
            Calculator_Terminate( pStateMachine );
          }
          else if ( Calculator_IsIn_EnterFractions_State( pStateMachine ) )
          {
            Calculator_Terminate( pStateMachine );
          }
          else if ( Calculator_IsIn_EnterExponents_State( pStateMachine ) )
          {
            Calculator_Terminate( pStateMachine );
          }
          else
          {
            Calculator_Terminate( pStateMachine );
          }
        }
        else if ( Calculator_IsIn_Error_State( pStateMachine ) )
        {
          if ( Calculator_IsIn_Faulty_State( pStateMachine ) )
          {
            Calculator_Terminate( pStateMachine );
          }
          else if ( Calculator_IsIn_DivisionByZero_State( pStateMachine ) )
          {
            Calculator_Terminate( pStateMachine );
          }
          else if ( Calculator_IsIn_Overflow_State( pStateMachine ) )
          {
            Calculator_Terminate( pStateMachine );
          }
          else
          {
            Calculator_Terminate( pStateMachine );
          }
        }
        else
        {
          doneProcessingUnit = false;
        }
      }

      if ( Calculator_IsIn_Output_Region( pStateMachine ) )
      {
        doneOutput = true;

        if ( Calculator_IsIn_CalculatorDisplay_State( pStateMachine ) )
        {
          Display_Run_SwitchOff( &pStateMachine->subsm.CalculatorDisplay );
          Calculator_Terminate( pStateMachine );
        }
        else
        {
          doneOutput = false;
        }
      }

      doneMain = doneInput || doneProcessingUnit || doneOutput;

      if( ! doneMain )
      {
        doneMain = true;

        Calculator_Terminate( pStateMachine );
      }
    }

    pthread_mutex_unlock( &pStateMachine->guard );
  }
}

