static void Calculator_Finalize_Main(S_SM_Calculator_t* pStateMachine )
{
  if ( Calculator_IsIn_Main_Region( pStateMachine ) )
  {
    pStateMachine->runningState.Main = E_Calculator_final;
  }
}

static void Calculator_Finalize_Input(S_SM_Calculator_t* pStateMachine )
{
  if ( Calculator_IsIn_Input_Region( pStateMachine ) )
  {
    pStateMachine->runningState.Input = E_Calculator_Input_final;
  }
}

static void Calculator_Finalize_ProcessingUnit(S_SM_Calculator_t* pStateMachine )
{
  if ( Calculator_IsIn_ProcessingUnit_Region( pStateMachine ) )
  {
    if ( Calculator_IsIn_Process_State( pStateMachine ) )
    {
      if ( pStateMachine->doFunctionHandler.Process.threadHandle != 0 && ! pthread_equal( pStateMachine->doFunctionHandler.Process.threadHandle, pthread_self( ) ) )
      {
        pthread_cancel( pStateMachine->doFunctionHandler.Process.threadHandle );
        pStateMachine->doFunctionHandler.Process.threadHandle = 0;
      }
    }
    pStateMachine->runningState.ProcessingUnit = E_Calculator_ProcessingUnit_final;
  }
}

static void Calculator_Finalize_Output(S_SM_Calculator_t* pStateMachine )
{
  if ( Calculator_IsIn_Output_Region( pStateMachine ) )
  {
    pStateMachine->runningState.Output = E_Calculator_Output_final;
  }
}

static void Calculator_Terminate( S_SM_Calculator_t* pStateMachine )
{
  Calculator_Finalize_Main( pStateMachine );
}

static void Calculator_Init_Main( S_SM_Calculator_t* pStateMachine )
{
  // fork destination -> `:Input:CalculatorKeypad`
  pStateMachine->runningState.Main = E_Calculator_Main;
  Calculator_Init_CalculatorKeypad( pStateMachine );
  pStateMachine->runningState.Input = E_Calculator_CalculatorKeypad;

  // fork destination -> `:ProcessingUnit:Process`
  pStateMachine->runningState.Main = E_Calculator_Main;
  Calculator_Enter_Process( pStateMachine );
  Calculator_Init_Process( pStateMachine );

  // fork destination -> `:Output:CalculatorDisplay`
  pStateMachine->runningState.Main = E_Calculator_Main;
  Calculator_Init_CalculatorDisplay( pStateMachine );
  pStateMachine->runningState.Output = E_Calculator_CalculatorDisplay;
}

static void Calculator_Init_CalculatorKeypad( S_SM_Calculator_t* pStateMachine )
{
  Keypad_Start( &pStateMachine->subsm.CalculatorKeypad );
}

static void Calculator_Init_Process( S_SM_Calculator_t* pStateMachine )
{
  Calculator_ResetAccumulations( pStateMachine, &pStateMachine->instanceData, &pStateMachine->instanceData );
  Calculator_NotifyDisplayUpdate( pStateMachine, &pStateMachine->instanceData, &pStateMachine->instanceData );

  Calculator_Enter_EnterBaseDigits( pStateMachine );
  pStateMachine->runningState.ProcessingUnit = E_Calculator_EnterBaseDigits;
}

static void Calculator_Init_CalculatorDisplay( S_SM_Calculator_t* pStateMachine )
{
  Display_Start( &pStateMachine->subsm.CalculatorDisplay );
}

static void* Calculator_DoAction_Process( void* statemachinePtr )
{
  S_SM_Calculator_t* pStateMachine = (S_SM_Calculator_t*)statemachinePtr;

  Calculator_CheckErrorStatus( pStateMachine, &pStateMachine->instanceData, &pStateMachine->instanceData );

  pthread_mutex_lock( &pStateMachine->guard );

  if ( Calculator_IsIn_Process_State( pStateMachine ) )
  {
    if ( Calculator_HasDivisionByZeroError( pStateMachine, &pStateMachine->instanceData ) )
    {
      Calculator_Exit_Process( pStateMachine );
      Calculator_Enter_DivisionByZero( pStateMachine );
      pStateMachine->runningState.ProcessingUnit = E_Calculator_DivisionByZero;
    }
    else if ( Calculator_HasOverflowError( pStateMachine, &pStateMachine->instanceData ) )
    {
      Calculator_Exit_Process( pStateMachine );
      Calculator_Enter_Overflow( pStateMachine );
      pStateMachine->runningState.ProcessingUnit = E_Calculator_Overflow;
    }
    else if ( Calculator_IsFaulty( pStateMachine, &pStateMachine->instanceData ) )
    {
      Calculator_Exit_Process( pStateMachine );
      Calculator_Enter_Faulty( pStateMachine );
      pStateMachine->runningState.ProcessingUnit = E_Calculator_Faulty;
    }
    else
    {
      /* left blank. the else block at the end is required by MISRA */
    }
  }

  pthread_mutex_unlock( &pStateMachine->guard );

  return NULL;
}

static void Calculator_Enter_Process(S_SM_Calculator_t* pStateMachine )
{
  pStateMachine->runningState.ProcessingUnit = E_Calculator_Process;

  Calculator_ResetAccumulations( pStateMachine, &pStateMachine->instanceData, &pStateMachine->instanceData );

  pStateMachine->doFunctionHandler.Process.threadHandle = Calculator_LaunchDoAction( Calculator_DoAction_Process, pStateMachine, pStateMachine->doFunctionHandler.Process.stack, sizeof( pStateMachine->doFunctionHandler.Process.stack) );
}

static void Calculator_Enter_EnterBaseDigits(S_SM_Calculator_t* pStateMachine )
{
  pStateMachine->runningState.ProcessingUnit = E_Calculator_EnterBaseDigits;

  Calculator_ClearFraction( pStateMachine, &pStateMachine->instanceData, &pStateMachine->instanceData );

  Calculator_ClearExponent( pStateMachine, &pStateMachine->instanceData, &pStateMachine->instanceData );

  Calculator_NotifyDisplayUpdate( pStateMachine, &pStateMachine->instanceData, &pStateMachine->instanceData );
}

static void Calculator_Enter_EnterFractions(S_SM_Calculator_t* pStateMachine )
{
  pStateMachine->runningState.ProcessingUnit = E_Calculator_EnterFractions;

  Calculator_ClearExponent( pStateMachine, &pStateMachine->instanceData, &pStateMachine->instanceData );

  Calculator_SetFractionEntry( pStateMachine, &pStateMachine->instanceData, &pStateMachine->instanceData );

  Calculator_NotifyDisplayUpdate( pStateMachine, &pStateMachine->instanceData, &pStateMachine->instanceData );
}

static void Calculator_Enter_EnterExponents(S_SM_Calculator_t* pStateMachine )
{
  pStateMachine->runningState.ProcessingUnit = E_Calculator_EnterExponents;

  Calculator_SetExponentEntry( pStateMachine, &pStateMachine->instanceData, &pStateMachine->instanceData );

  Calculator_NotifyDisplayUpdate( pStateMachine, &pStateMachine->instanceData, &pStateMachine->instanceData );
}

static void Calculator_Enter_Faulty(S_SM_Calculator_t* pStateMachine )
{
  pStateMachine->runningState.ProcessingUnit = E_Calculator_Faulty;

  Calculator_NotifyFaultError( pStateMachine, &pStateMachine->instanceData, &pStateMachine->instanceData );
}

static void Calculator_Enter_DivisionByZero(S_SM_Calculator_t* pStateMachine )
{
  pStateMachine->runningState.ProcessingUnit = E_Calculator_DivisionByZero;

  Calculator_NotifyDivisionByZeroError( pStateMachine, &pStateMachine->instanceData, &pStateMachine->instanceData );
}

static void Calculator_Enter_Overflow(S_SM_Calculator_t* pStateMachine )
{
  pStateMachine->runningState.ProcessingUnit = E_Calculator_Overflow;

  Calculator_NotifyOverflowError( pStateMachine, &pStateMachine->instanceData, &pStateMachine->instanceData );
}

static void Calculator_Exit_CalculatorKeypad(S_SM_Calculator_t* pStateMachine )
{
  pStateMachine->runningState.Input = E_Calculator_CalculatorKeypad;

  Keypad_Deinitialize( &pStateMachine->subsm.CalculatorKeypad );
}

static void Calculator_Exit_Process(S_SM_Calculator_t* pStateMachine )
{
  pStateMachine->runningState.ProcessingUnit = E_Calculator_Process;

  Calculator_HaltDoAction( pStateMachine->doFunctionHandler.Process.threadHandle );
  pStateMachine->doFunctionHandler.Process.threadHandle = 0;
}

static void Calculator_Exit_Error(S_SM_Calculator_t* pStateMachine )
{
  pStateMachine->runningState.ProcessingUnit = E_Calculator_Error;

  Calculator_NotifyReset( pStateMachine, &pStateMachine->instanceData, &pStateMachine->instanceData );
}

static void Calculator_Exit_CalculatorDisplay(S_SM_Calculator_t* pStateMachine )
{
  pStateMachine->runningState.Output = E_Calculator_CalculatorDisplay;

  Display_Deinitialize( &pStateMachine->subsm.CalculatorDisplay );
}

static pthread_t Calculator_LaunchDoAction( void *(*threadStart)(void *), S_SM_Calculator_t* pStateMachine, void *stackAddr, size_t stackSize )
{
  pthread_t thr = 0;

  if ( threadStart != NULL )
  {
    pthread_attr_t attr;

    int err = pthread_attr_init( &attr );
    if ( err == 0 )
    {
      err = pthread_attr_setdetachstate( &attr, PTHREAD_CREATE_DETACHED );

      if ( err == 0 )
      {
        err = pthread_attr_setinheritsched( &attr, PTHREAD_EXPLICIT_SCHED );
      }

      if ( err == 0 )
      {
        err = pthread_attr_setstack( &attr, stackAddr, stackSize );
        err = 0;
      }

      if ( err == 0 )
      {
        err = pthread_create( &thr, &attr, threadStart, pStateMachine);
      }

      if ( err != 0 )
      {
        thr = 0;
      }

      pthread_attr_destroy( &attr );
    }
  }

  return thr;
}

static void Calculator_HaltDoAction( pthread_t thr )
{
  if ( thr != 0 && ! pthread_equal( thr, pthread_self( ) ) )
  {
    pthread_cancel( thr );
  }
}

