static void Crossroad_Init_Main( S_SM_Crossroad_t* pStateMachine )
{
  // fork destination -> `:RgLane1:Lane1`
  pStateMachine->runningState.Main = E_Crossroad_Main;
  Crossroad_Init_Lane1( pStateMachine );
  pStateMachine->runningState.RgLane1 = E_Crossroad_Lane1;

  // fork destination -> `:RgLane2:Lane2`
  pStateMachine->runningState.Main = E_Crossroad_Main;
  Crossroad_Init_Lane2( pStateMachine );
  pStateMachine->runningState.RgLane2 = E_Crossroad_Lane2;

  // fork destination -> `:RgLane3:Lane3`
  pStateMachine->runningState.Main = E_Crossroad_Main;
  Crossroad_Init_Lane3( pStateMachine );
  pStateMachine->runningState.RgLane3 = E_Crossroad_Lane3;

  // fork destination -> `:RgLane4:Lane4`
  pStateMachine->runningState.Main = E_Crossroad_Main;
  Crossroad_Init_Lane4( pStateMachine );
  pStateMachine->runningState.RgLane4 = E_Crossroad_Lane4;

  // fork destination -> `:RgPedestrianLanes:PedestrianLanes`
  pStateMachine->runningState.Main = E_Crossroad_Main;
  Crossroad_Init_PedestrianLanes( pStateMachine );
  pStateMachine->runningState.RgPedestrianLanes = E_Crossroad_PedestrianLanes;

  // fork destination -> `:RgTrafficLight1:TrafficLight1`
  pStateMachine->runningState.Main = E_Crossroad_Main;
  Crossroad_Init_TrafficLight1( pStateMachine );
  pStateMachine->runningState.RgTrafficLight1 = E_Crossroad_TrafficLight1;

  // fork destination -> `:RgTrafficLight2:TrafficLight2`
  pStateMachine->runningState.Main = E_Crossroad_Main;
  Crossroad_Init_TrafficLight2( pStateMachine );
  pStateMachine->runningState.RgTrafficLight2 = E_Crossroad_TrafficLight2;

  // fork destination -> `:RgTrafficLight3:TrafficLight3`
  pStateMachine->runningState.Main = E_Crossroad_Main;
  Crossroad_Init_TrafficLight3( pStateMachine );
  pStateMachine->runningState.RgTrafficLight3 = E_Crossroad_TrafficLight3;

  // fork destination -> `:RgTrafficLight4:TrafficLight4`
  pStateMachine->runningState.Main = E_Crossroad_Main;
  Crossroad_Init_TrafficLight4( pStateMachine );
  pStateMachine->runningState.RgTrafficLight4 = E_Crossroad_TrafficLight4;

  // fork destination -> `:RgPedestrianLights:PedestrianLights`
  pStateMachine->runningState.Main = E_Crossroad_Main;
  Crossroad_Init_PedestrianLights( pStateMachine );
  pStateMachine->runningState.RgPedestrianLights = E_Crossroad_PedestrianLights;
}

