void ExecutionDirector_Run_TakeControl( S_SM_ExecutionDirector_t* pStateMachine )
{
  if ( pStateMachine != NULL )
  {
    __attribute__( ( unused ) ) bool doneMain = false;

    pthread_mutex_lock( &pStateMachine->guard );

    if ( ExecutionDirector_IsIn_StandBy_State( pStateMachine ) )
    {
      ExecutionDirector_SetControlledMode( pStateMachine, &pStateMachine->instanceData, &pStateMachine->instanceData );

      ExecutionDirector_Exit_StandBy( pStateMachine );
      ExecutionDirector_Enter_SwitchSystem( pStateMachine );
      ExecutionDirector_Enter_GoesToOperational( pStateMachine );
      pStateMachine->runningState.Main = E_ExecutionDirector_GoesToOperational;
    }

    if ( ExecutionDirector_IsIn_GoesToOperational_State( pStateMachine ) )
    {
      ExecutionDirector_SetControlledMode( pStateMachine, &pStateMachine->instanceData, &pStateMachine->instanceData );
    }
    else if ( ExecutionDirector_IsIn_GoesToStandby_State( pStateMachine ) )
    {
      ExecutionDirector_SetControlledMode( pStateMachine, &pStateMachine->instanceData, &pStateMachine->instanceData );

      ExecutionDirector_Exit_GoesToStandby( pStateMachine );
      ExecutionDirector_Enter_GoesToOperational( pStateMachine );
      pStateMachine->runningState.Main = E_ExecutionDirector_GoesToOperational;
    }
    else if ( ExecutionDirector_IsIn_SwitchControlState_State( pStateMachine ) )
    {
      ExecutionDirector_SetControlledMode( pStateMachine, &pStateMachine->instanceData, &pStateMachine->instanceData );
    }
    else if ( ExecutionDirector_IsIn_Uncontrolled_State( pStateMachine ) )
    {
      if ( ExecutionDirector_IsIn_CheckRequests_State( pStateMachine ) )
      {
        ExecutionDirector_SetControlledMode( pStateMachine, &pStateMachine->instanceData, &pStateMachine->instanceData );

        ExecutionDirector_Exit_CheckRequests( pStateMachine );
        ExecutionDirector_Exit_Operational( pStateMachine );
        ExecutionDirector_Enter_SwitchControlState( pStateMachine );
        pStateMachine->runningState.Main = E_ExecutionDirector_SwitchControlState;
      }
      else
      {
        ExecutionDirector_SetControlledMode( pStateMachine, &pStateMachine->instanceData, &pStateMachine->instanceData );

        ExecutionDirector_Exit_Operational( pStateMachine );
        ExecutionDirector_Enter_SwitchControlState( pStateMachine );
        pStateMachine->runningState.Main = E_ExecutionDirector_SwitchControlState;
      }
    }
    else
    {
      /* left blank. the else block at the end is required by MISRA */
    }
    pthread_mutex_unlock( &pStateMachine->guard );
  }
}

